import React, { Component } from 'react'

class Counter extends Component {

    constructor(props) {
        super(props)

        this.state = {
            count: 0
        }
    }

    updateCounter(updateBy) {
        this.setState((prevState) => ({
            count: prevState.count + updateBy
        }))
    }

    resetCounter() {
        this.setState({
            count: 0
        })
    }

    render() {
        return (
            <div>
                <h1>Counter is : {this.state.count}</h1>
                <button onClick={() => this.updateCounter(5)}>Increment</button>       <button onClick={() => this.resetCounter()}>Reset</button>
            </div>
        )
    }
}

export default Counter
