import React, { Component } from 'react'

class Counter extends Component {

    constructor(props) {
        super(props)

        this.state = {
            count: 0
        }
    }

    updateCounter() {
        this.setState({
            count: this.state.count + 1
        })
    }

    render() {
        return (
            <div>
                <h1>Counter is : {this.state.count}</h1>
                <button onClick={() => this.updateCounter()}>Increment</button>
            </div>
        )
    }
}

export default Counter
